/** @format */

@import url(uc-globals.css);
@import url(uc-panels.css);
@import url(uc-app-menu.css);
@import url(uc-findbar.css);
@import url(uc-ctrl-tab.css);
@import url(uc-bookmarks.css);
@import url(uc-fullscreen.css);
