@import url(compact-bookmarks.css);
@import url(sliding-bookmarks-bar.css);
:root {
	--panel-separator-zap-gradient: linear-gradient(
		to left,
		#ff880021,
		#551dfc,
		#2525ff,
		#ff880021
	) !important;
	--new-gradient: linear-gradient(
		to right,
		#4048bf,
		#2525ff,
		#ff88008a,
		#ff8800
	) !important;
	--toolbarbutton-inner-padding: 3px !important;
	--tab-block-margin: 3px !important;
	--tab-min-height: 32px !important;
	--tabs-shadow-size: 1px !important;
