#userContext-icons::before {
	box-sizing: border-box;
	display: block;
	position: absolute;
	content: "";
	left: 0;
	top: 0;
	width: 100%;
	height: 100%;
	outline: 1px solid
		color-mix(
			in srgb,
			var(--identity-icon-color) 80%,
			var(--toolbar-field-border-color)
		);
	border-radius: 10px;
	outline-offset: -1px;
	pointer-events: none;
}

