.tabbrowser-tab[multiselected]:-moz-lwtheme {
	color: var(--lwt-tab-text) !important;
	--lwt-tab-line-color: transparent !important;
}

/* Favicon icon when mouse over to pinned inactive tab grayscale(15%) */

.tabbrowser-tab:hover .tab-icon-image[pinned] {
	filter: drop-shadow(16px 16px 10px black) !important;
}

/* Favicon icon on pinned inactive tab grayscale(35%) */

.tabbrowser-tab .tab-icon-image[pinned] {
	filter: contrast(175%) brightness(103%) !important;
}

/* Favicon icon when mouse over over inactive tab grayscale(15%) */

.tabbrowser-tab:hover .tab-icon-image:not([selected="true"]):not([pinned]) {
	filter: drop-shadow(16px 16px 10px black) !important;
}

/* Gray out the favicon icon on the inactive tab */

.tabbrowser-tab .tab-icon-image:not([selected="true"]):not([pinned]) {
	filter: opacity(70%) saturate(30%) !important;
}

/* Tab labels */

#main-window[lwthemetextcolor="dark"] tab {
	color: #333 !important;
}

#main-window[lwthemetextcolor="dark"] tab:hover {
	color: #000 !important;
}

#main-window[lwthemetextcolor="dark"] tab[selected] {
	color: #000 !important;
}

/* This is kinda weird, but it makes the horizontal line between tabs and nav-bar render "inside" nav-bar thus tabs are more visibily separated even if there is less space there */

#nav-bar {
	box-shadow: inset 0 var(--tabs-shadow-size) 0 var(--lwt-tabs-border-color) !important;
}

.private-browsing-indicator {
	display: none !important;
}

.accessibility-indicator {
	display: none !important;
}

/* Small fix for Firefox complete themes (added in 2016/03/09) */

.private-browsing-indicator,
#private-browsing-indicator-titlebar,
#private-browsing-indicator,
.private-browsing-indicator {
	visibility: collapse !important;
}

/* show "Private" labeln*/

:root[titlemodifier="(Private Browsing)"] #fxa-toolbar-menu-button::before {
	content: "Private" !important;
	display: -moz-box !important;
	margin-inline-start: 12px !important;
	margin-inline-end: 6px !important;
}

/* dark theme header *

:root[lwthemetextcolor="bright"] #navigator-toolbox,
:root[lwt-default-theme-in-dark-mode] #navigator-toolbox {
    -moz-appearance: -moz-mac-vibrant-titlebar-dark !important;
}


/***inactive*/

.tabbrowser-tab:not([visuallyselected="true"]):not([multiselected]):not(:hover)
	.tab-icon-image {
	opacity: 1 !important;
	filter: grayscale(0.5), brightness(70%), contrast(20%) !important;
}

/* Glowing line at top. */

.tabbrowser-tab > .tab-stack > .tab-background > .tab-context-line {
	background-color: var(--identity-icon-color) !important;
	height: 1px !important;
	box-shadow: 0px 3px 9px 0px var(--identity-icon-color) !important;
	border-radius: 10px !important;
}

/*rework en la Linea de Contenedores-BordeFavicon*/

.tab-icon-pending:not([selected], [pinned]),
.tab-icon-pending:not([pinned]),
.tab-sharing-icon-overlay:not([selected], [pinned]),
.tab-throbber[busy],
.tab-icon-image:not([selected], [pinned]) {
	box-shadow: 0 -1px 0px var(--identity-icon-color, #00000000),
		0 1px 0px var(--identity-icon-color, #00000000) !important;
	border-radius: 10px !important;
	padding: 2px !important;
	margin-left: -1px !important;
	margin-right: 6px !important;
}

.tab-icon-pending[selected],
.tab-sharing-icon-overlay[selected],
.tab-icon-image[selected] {
	outline: 1px solid var(--identity-icon-color, #00000000) !important;
	border-radius: 10px !important;
	padding: 2px !important;
	margin-left: -1px !important;
	margin-right: 6px !important;
}

.tab-icon-pending:not([selected])[pinned],
.tab-sharing-icon-overlay:not([selected])[pinned],
.tab-throbber[busy][pinned],
.tab-icon-image:not([selected])[pinned] {
	box-shadow: 0 -1px 0px var(--identity-icon-color, #00000000),
		0 1px 0px var(--identity-icon-color, #00000000) !important;
	padding: 2px !important;
	border-radius: 10px !important;
}

.tab-icon-pending[pinned],
.tab-sharing-icon-overlay[pinned],
.tab-icon-image[pinned],
.tab-throbber:is([busy][pinned]) {
	margin-right: -1px !important;
	margin-left: -1px !important;
}

/*Nuevos margenes - CargandoPágina*

.tab-throbber[busy]:not([pinned]) {
    border-radius: 0px !important;
    padding: 0 !important;
    height: 20px !important;
    margin-left: 1px !important;
    margin-right: 8px !important;
    opacity: 1 !important;
}

.tab-throbber:is([busy][pinned]) {
    border-radius: 0px !important;
    padding: 0px !important;
    height: 20px !important;
    margin-left: 1px !important;
    margin-right: 1px !important;
}

/*Vibrancy-for-macOS*/

#main-window {
	background: transparent !important;
}

#TabsToolbar,
.table-widget-empty-text {
	-moz-appearance: -moz-mac-source-list !important;
}

#TabsToolbar:-moz-lwtheme-brighttext,
.table-widget-empty-text:-moz-lwtheme-brighttext {
	-moz-appearance: -moz-mac-source-list !important;
}

.tabbrowser-tab[selected="true"] .tab-label:-moz-lwtheme-brighttext {
	color: white !important;
}

.tabbrowser-tab .tab-label:-moz-lwtheme-brighttext {
	color: rgba(255, 255, 255, 0.7) !important;
}

.tabbrowser-tab[selected="true"] .tab-background:-moz-lwtheme-brighttext {
	box-shadow: inset 0em -4em 0em var(--toolbar-bgcolor) !important;
}

#navigator-toolbox {
	background-color: transparent !important;
}

.tabbrowser-tab[usercontextid]
	> .tab-stack
	> .tab-background
	> .tab-context-line {
	background-image: linear-gradient(
		to right,
		transparent 5%,
		var(--identity-tab-color) 5%,
		var(--identity-tab-color) 95%,
		transparent 95%
	) !important;
	height: 2px !important;
	margin-bottom: 0px !important;
	border-radius: 10px !important;
}

:root[tabsintitlebar]:is(:not(:-moz-lwtheme), [lwt-default-theme-in-dark-mode])
	#navigator-toolbox-background {
	-moz-appearance: -moz-mac-source-list !important;
}

:root:is(:not(:-moz-lwtheme), [lwt-default-theme-in-dark-mode])
	#navigator-toolbox-background {
	-moz-appearance: -moz-mac-source-list !important;
}

#main-window:is(:not(:-moz-lwtheme), [lwt-default-theme-in-dark-mode])
	#navigator-toolbox {
	background-color: transparent !important;
}

/***  Glowing line at bottom. */

.tabbrowser-tab > .tab-stack > .tab-background > .tab-context-line {
	background-color: var(--identity-icon-color) !important;
	height: 2px !important;
	border-radius: 10px !important;
	box-shadow: 0px 0px 18px 2px var(--identity-icon-color) !important;
}

/* Bookmarks bar with BLUR need layout.css.backdrop-filter.enabled = true rgba(50, 50, 52, 0.85)*/

#PersonalToolbar {
	background-image: url(./noise-512x512.png) !important;
	background-color: transparent !important;
	-webkit-backdrop-filter: blur(32px) !important;
	backdrop-filter: blur(32px) !important;
}

/* Blend Inactive tabs favicon */

#TabsToolbar
	#tabbrowser-tabs
	.tabbrowser-tab[pending]
	.tab-content:not(:hover)
	.tab-icon-image {
	filter: grayscale(20%) !important;
	transition: filter 0.2s ease-in;
	opacity: 0.8 !important;
}

.tabbrowser-tab[usercontextid] .tab-content {
	border-bottom: 2px solid var(--identity-icon-color) !important;
}

.tabbrowser-tab[usercontextid] .tab-content > * {
	margin-bottom: -2px !important;
}
